function PortfolioItem ({ project,URI }) {

const rollIn = event => event.target.querySelector('h3').classList.add('roll')

// function rollIn(event) {
//     let projectTitle = event.target.closest('h3')
//     // projectTitle.classList.add('roll')
// }
function rollOut(event) {
    let projectTitle = event.target.closest('h3')
    // projectTitle.classList.remove('roll')
    console.log(event.target.querySelector('h3'))
}

function toggleModal(event) {
    let modalOverlay = document.querySelector('.my-modal-overlay')
    modalOverlay.classList.add('open')
}

    return (
        <div className="col-md-4">
           <div className="portfolio-card m-4 p-3" style={{backgroundImage:`url(${URI + project.Image.formats.small.url})`}} onMouseEnter={event => rollIn(event)} onMouseLeave={event => rollOut(event)} onClick={event => toggleModal(event)}>
           <h3>{project.Title}</h3>
           </div>
           <div className="my-modal-overlay">
                <div className="my-modal-container"></div>
            </div>
        </div>
    )
}

export default PortfolioItem
