import { useEffect, useState } from 'react'
import { getSkills, getAbout, URI } from '../../Helpers'

function About() {

    const [state, setState] = useState({
        about:[],
        skills:[],
        loading:false
    })

    useEffect(() => {
        setState(prevState => ({
            ...prevState,
            loading:true
        }))

        getAbout()
        .then( items => {
            if(state.loading) {
                setState(prevState => ({
                    ...prevState,
                    skills:items
                }))
            }
        })

        getSkills()
        .then( items => {
            if(state.loading) {
                setState(prevState => ({
                    ...prevState,
                    skills:items
                }))
            }
        })
        setState(prevState => ({
            ...prevState,
            loading:false
        }))
    }, [state.loading])

console.log(state)

    // const trimmedURI = loading ?  "" : URI.substring(0,URI.length-1)
    // const {url, name} = loading ? '' : about.profileImage.formats.medium

    return (
        <div id="about">

            <div className="container">
                <div className="row">
                    <div className="col-md-4" >
                        <div id="stay-put" >
                            {/* {loading ?  '' : <img src={trimmedURI + url} alt={name} />} */}

                        </div>

                    </div>
                    <div className="col-md-8">
                        <h1>Lorem Ipsum</h1>
                        {state.about.description}

                        <hr />

                        <h3>Lorem</h3>
                        <ul>
                            {state.skills.map( item => {
                                return (
                                    <li key={item.id}>{item.entry}</li>
                                )
                            })}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default About
