import { getBlogs, URI } from '../../Helpers'
import { useEffect, useState } from 'react'


function Blog () {

    const [state, setState] = useState({
        loading:false,
        blogs:[],
        featured:[]
    })

    useEffect(() => {
        setState(prevState => ({ ...prevState, loading:true }))

        getBlogs()
        .then(blogs => {
            if(state.loading) {
                setState(prevState => ({ ...prevState, blogs:blogs }))
                getFeatured(blogs)
            }
        })
        .catch( err => console.error(err) ) 


        return () => setState(prevState => ({ ...prevState, loading:false }))

    },[state.loading])

    function getFeatured (items) {
        let featuredPost = items.filter( post => post.featured )
        console.log(featuredPost)
    }

    return(
            <div id="blog-featured">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-md-6">
                        <h1>Blog</h1>
                        </div>
                        <div className="col-md-6">
                            <h2>Blog image</h2>
                        </div>
                    </div>
                </div>
            </div> 
    )
}

export default Blog
