import PortfolioItem from "./PortfolioItem"
import { useEffect, useState } from 'react'
import { getProjects, URI } from '../../Helpers'


function Portfolio () {
    const [state, setState] = useState({
        loading:false,
        projects:[]
    })

    useEffect(() => {
        setState(prevState => ({ ...prevState, loading:true }))

        getProjects()
        .then(projects => {
            if(state.loading) {
                setState(prevState => ({ ...prevState, projects:projects }))
            }
        })
        .catch( err => console.error(err) )

        return () => {
            setState(prevState => ({ ...prevState, loading:false }
                ))
                cardFunctionalities();
            }
    },[state.loading])

    function cardFunctionalities() {
        const portfolioCard = document.querySelector('.portfolio-card')
        console.log(portfolioCard)
        console.log(document.querySelector('.portfolio-card'))
        if(portfolioCard) {
            portfolioCard.addEventListener('mouseenter', () => {
                portfolioCard.querySelector('h3').classList.add('roll')
            })
            portfolioCard.addEventListener('mouseleave', () => {
                portfolioCard.querySelector('h3').classList.remove('roll')
            })
            portfolioCard.addEventListener('click', () => {
                console.log('click')
            })
        }

    }

    return (
        <section id="portfolio" className="py-4 text-center">
           <div className="container-fluid" id="cover">
           <h2 className="text-center">
                Lorem Ipsum
            </h2>
            <div className="row">
               {state.projects.map(project => <PortfolioItem project={project} key={project.id} URI={URI}/> )}
           </div>
           </div>
        </section>
    )
}

export default Portfolio
